def minimum_travel_time(n, m, railways):
    dist = [[1] * (n + 1) for _ in range(n + 1)]
    for (a, b) in railways:
        dist[a][b] = dist[b][a] = 2
    x = 3 + dist[1][n]
    visited = [0] * (n + 1)
    d = [n + 1] * (n + 1)
    d[1] = 0
    i = 1
    while i != n:
        visited[i] = 1
        for j in range(1, n + 1):
            if visited[j] == 0 and dist[i][j] == x and (d[j] > d[i] + 1):
                d[j] = d[i] + 1
        meu = n + 1
        for j in range(1, n + 1):
            if visited[j] == 0 and d[j] < meu:
                meu = d[j]
                i = j
        if meu == n + 1:
            return -1
        elif i == n:
            return d[n]
    return d[n]

