    if a > b:
        (a, b) = (b, a)
    diff = b - a
    if diff == 0:
        return 0
    min_lcm = float('inf')
    best_k = 0
    divs = []
    i = 1
    while i * i <= diff:
        if diff % i == 0:
            divs.append(i)
            if i == diff // i:
                divs.append(diff // i)
        i += 1
    divs.sort()
