def calculate_min_marks_below_water(n, above):
    total = [x - 1 for x in above]
    for i in range(n - 2, -1, -1):
        total[i] = max(total[i], total[i + 1] - 1)
    for i in range(1, n):
        total[i] = max(total[i], total[i - 1])
    below = [t - a - 1 for (t, a) in zip(total, above)]
    return sum(below)

