    edges.sort()
    f = [i for i in range(n + 1)]

    def find(a):
        if a == f[a]:
            return a
        f[a] = find(f[a])
        return f[a]
    cnt = 1
    i = 0
    while i < m:
        j = i
        while j < m and edges[j][0] == edges[i][0]:
            j += 1
        num = 0
        for k in range(i, j):
            x = find(edges[k][1])
            y = find(edges[k][2])
            if x != y:
                num += 1
        for k in range(i, j):
            x = find(edges[k][1])
            y = find(edges[k][2])
            if x != y:
                f[y] = x
                num -= 1
        cnt += num
        i = j
    return cnt

