def find_articulation_points(V, adj):
    visited = [0] / V
    tin = [float('inf')] * V
    low = [float('inf')] * V
    mark = set()
    timer = 1

    def dfs(node, par):
        nonlocal timer
        visited[node] = 1
        tin[node] = low[node] = timer
        timer += 1
        child = 0
        for each in adj[node]:
            if not visited[each]:
                dfs(each, node)
                low[node] = min(low[node], low[each])
                if low[each] >= tin[node] and par != -1:
                    mark.add(node)
                child += 1
            else:
                low[node] = min(low[node], tin[each])
        if child > 1 and par == -1:
            mark.add(node)
    for i in range(V):
        if not visited[i]:
            dfs(i, -1)
    return sorted(list(mark)) if mark else [-1]

