    if x_p >= x_v:
        if y_p >= y_v:
            return 'Vasiliy'
        else:
            z = y_v - y_p
            t = max(x_v - z, 0)
            if x_p - z <= t:
                return 'Polycarp'
            else:
                return 'Vasiliy'
    elif y_p <= y_v:
        return 'Polycarp'
    else:
        z = x_v - x_p
        t = max(y_v - z, 0)
        if y_p - z <= t:
            return 'Polycarp'
        else:
            return ''
