import math

def calculate_prime_factorials(n):

    def is_prime(num):
        if num <= 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True

    def factorial(num):
        prod = 1
        while num != 1:
            prod *= num
            num -= 1
        return prod
    prime_factorials = []
    for num in range(2, n + 1):
        if is_prime(num):
            prime_factorials.append(factorial(num))
    return prime_factorials

