def compute_gcd_lcm(A: int, B: int) -> tuple:
    """"""

    def GCD(x, y):
        while y:
            (x, y) = (y, x % y)
        return x

    def LCM(x, y):
        lcm = x * y // GCD(x, y)
        return lcm
    gcd_value = GCD(A, B)
    lcm_value = LCM(A, B)
    return (gcd_value, lcm_value)
