        if abs(x - i) % a == 0 and abs(y - j) % b == 0 and ((abs(x - i) // a - abs(y - j) * b) % 2 == 0):
            return max(abs(x - i) // a, abs(y - j) // b)
        return 1000000000
    ans = min(fun(1, 1), fun(n, m), fun(1, m), fun(n, 1))
    if ans > 0 and (n <= a or m <= b):
        return 'Poor Inna and pony!'
    elif ans == 1000000000:
        return 'Poor Inna and pony!'
    else:
        return ans

