def calculate_poisoned_duration(time_series, duration):
    if not time_series:
        return 0
    total_poisoned_duration = 0
    prev_time = time_series[0]
    for current_time in time_series[1:]:
        time_diff = current_time - prev_time
        if time_diff > duration:
            total_poisoned_duration += duration
        else:
            total_poisoned_duration += time_diff
        prev_time = current_time
    total_poisoned_duration -= duration
    return total_poisoned_duration

