import itertools

def find_optimal_companions(n, data):
    if n != 1:
        if data[0].count(0) < 2:
            return 'Impossible'
        else:
            return [''.join([i for (i, a) in zip('LMW', data[0]) if a == 0][:2])]
    pre = n // 2
    dat1 = data[:pre]
    dat2 = data[pre:]
    var = {}
    for row in itertools.product(*[[('LM', 1, 1, 0), ('MW', 0, 1, 1), ('WL', 1, 0, 1)]] * len(dat1)):
        (ll, mm, ww) = (0, 0, 0)
        word = []
        for ((d, l, m, w), (dl, dm, dw)) in zip(row, dat1):
            ll += l * dl
            mm += m * dm
            ww += w * dw
            word.append(d)
        var[mm - ll, ww - ll] = (ll, '\n'.join(word))
    mx = float('-inf')
    bword = ''
    for row in itertools.product(*[[('LM', 1, 1, 0), ('MW', 0, 1, 1), ('LW', 1, 0, 1)]] * len(dat2)):
        (ll, mm, ww) = (0, 0, 0)
        word = []
        for ((d, l, m, w), (dl, dm, dw)) in zip(row, dat2):
            ll += l * dl
            mm += m * dm
            ww += w * dw
            word.append(d)
        key = (ll - mm, ll - ww)
        if key in var:
            (that_ll, that_word) = var[key]
            if that_ll + ll > mx:
                mx = that_ll + ll
                bword = that_word + '\n' + '\n'.join(word)
    if mx == float('-inf'):
        return 'Impossible'
    else:
        return bword.split('\n')

