def minimize_angry_comments(s, x, y):
    c0 = 1
    c1 = 0
    r = 0
    for i in s:
        if i == '0':
            c0 += 1
        else:
            r += c0 * x
            c1 += 1
    z0 = 0
    for i in range(len(s) - 1, -1, -1):
        if s[i] == '0':
            z0 += 1
        else:
            r += z0 * y
    ans = r
    l0 = 0
    l1 = 0
    for i in s:
        if i == '?':
            r -= l0 * x + c0 * y
            c1 -= 1
            r += l1 * y + c1 * x
            l0 += 1
        elif i == '0':
            l0 += 1
            c0 -= 1
        else:
            l1 += 1
            c1 -= 1
        ans = min(ans, r)
    return ans

