def find_balanced_grades(test_cases):
    results = []
    for (n, scores) in test_cases:
        k = n / 4
        sorted_scores = sorted(scores)
        a60 = (sorted_scores[k - 1], sorted_scores[k])
        a75 = (sorted_scores[2 * k - 1], sorted_scores[2 * k])
        a90 = (sorted_scores[3 * k - 1], sorted_scores[3 * k])
        if a60[0] == a60[1] or a75[0] == a75[1] or a90[0] == a90[1]:
            results.append((-1, -1, -1))
        else:
            results.append((a60[1], a75[1], a90[1]))
    return results

