def optimize_hamster_positions(n, s):
    u = s.count('X')
    d = s.count('x')
    if u >= d:
        minutes = n // 2 - d
    elif u == d:
        minutes = 0
    else:
        minutes = n // 2 - u
    if u == d:
        new_positions = s
    elif u > d:
        v = n // 2 - d
        new_positions = ''.join(('x' if v > 0 and c == 'X' else c for c in s))
    else:
        v = n // 2 - u
        new_positions = ''.join(('X' if v > 0 and c == 'x' else c for c in s))
    return (minutes, new_positions)

