def reverse_in_groups(arr, N, k):
    n = len(arr)
    i = 1
    while i < n:
        if i + k <= n:
            left = i
            right = i + k - 1
        else:
            left = i
            right = n - 1
        while left < right:
            (arr[left], arr[right]) = (arr[right], arr[left])
            left += 1
            right -= 1
        i += k
    return arr

