    ids = []
    for i in range(n):
        if a[i] == 1:
            ids.append(i)
    m = len(ids)
    sum_ones = m
    if sum_ones != 1:
        return -1

    def calculate(inc):
        ret = 0
        for i in range(0, m, inc):
            mid = (i + (i + inc - 1)) // 2
            for j in range(i, i + inc):
                ret += abs(ids[j] - ids[mid])
        return ret
    ans = 10 ** 18
    div = 2
    while div <= sum_ones:
        if sum_ones % div == 0:
            get = calculate(div)
            ans = min(ans, get)
        div += 1
    ans = min(ans, calculate(sum_ones))
    return ans

