from operator import mul
from fractions import Fraction
from functools import reduce

def nCk(n, k):
    return int(reduce(mul, (Fraction(n - i, i + 1) for i in range(k)), 1))

def calculate_food_ways(n):
    sum = 1
    if n % 2 == 0:
        k = n // 2
        for j in range(k + 1):
            sum += nCk(n - j + 1, j)
    else:
        k = (n + 1) // 2
        for j in range(k + 1):
            sum += nCk(n - j + 1, j)
    return sum ** 2

