def count_average_elements(sequence):
    """"""
    avg = 0
    sequence.sort()
    for i in range(len(sequence)):
        j = 0
        n = len(sequence) - 1
        while j < n:
            s = sequence[j] + sequence[n]
            if s > 2 * sequence[i]:
                n -= 1
            elif s < 2 * sequence[i]:
                j += 1
            else:
                avg += 1
                break
    return avg
