def longest_non_unique_freq_subarray(arr, n=None):
    if n is not None:
        n = len(arr)
    if n == 0:
        return 0
    freq = [0] * 101
    for num in arr:
        freq[num] += 1
    maxx = max(freq)
    amtOFmaxx = freq.count(maxx)
    if amtOFmaxx >= 2:
        return n
    must_apper = freq.index(maxx)
    ans = 0
    for j in range(1, 101):
        if j == must_apper:
            continue
        first_index = [10 ** 6] * (n + 1)
        first_index[0] = -1
        curr = 0
        for i in range(n):
            if arr[i] == must_apper:
                curr += 1
            elif arr[i] == j:
                curr -= 1
            ans = max(ans, i - first_index[curr])
            first_index[curr] = min(first_index[curr], i)
    return ans

