def max_possible_sum_after_flip(ai: list) -> int:
    """"""
    n = len(ai)
    s = sum(ai)
    h = False
    for j in range(n - 1):
        if ai[j] == -1 and ai[j + 1] == -1:
            h = True
            return s + 4
            break
    if not h:
        if ai.count(-1) == 0:
            return s - 4
        else:
            return s
