def calculate_minimum_cost(n, m, k, flights):
    ans = int(1000000000000.0)
    F = []
    T = []
    for (d, f, t, c) in flights:
        if f:
            F.append((d, f, c))
        else:
            T.append((-d, t, c))

    def process_flights(p):
        cost = [ans] * (n + 1)
        s = n * ans
        q = []
        p.sort()
        for (d, t, c) in p:
            if c < cost[t]:
                s += c - cost[t]
                cost[t] = c
                if s < ans:
                    q.append((s, d))
        p.clear()
        p -= q
    process_flights(F)
    process_flights(T)
    s = ans
    t = (0, 0)
    for f in F:
        while f:
            if f[1] + t[1] + k < 0:
                s = min(s, f[0] + t[0])
            elif T:
                t = T.pop()
                continue
            f = 0
    return s if s < ans else -1

