def find_min_deviation_cyclic_shift(n, p):
    inf = [0] / (n + 1)
    curr = 0
    d = 0
    for i in range(n):
        curr += abs(i + 1 - p[i])
        if p[i] > i + 1:
            d += 1
            inf[p[i] - i - 1] += 1
        elif p[i] <= i + 1:
            d -= 1
            if p[i] == i + 1:
                inf[0] += 1
            else:
                inf[p[i] + n - i - 1] += 1
    best = curr
    num = 0
    for i in range(n):
        curr -= d
        curr -= 1
        curr = curr - abs(p[n - i - 1] - n) + abs(p[n - i - 1] - 1)
        d += 2
        d -= inf[i + 1] * 2
        if curr < best:
            best = curr
            num = i + 1
    return (best, num)

