    INF = 1000000000000000001
    dp = [[INF] * 2 for _ in range(n)]
    dp[0][0] = 0
    dp[0][1] = c[0]
    for i in range(1, n):
        if strings[i - 1] <= strings[i] and dp[i - 1][0] < dp[i][0]:
            dp[i][0] = dp[i - 1][0]
        if strings[i - 1][::-1] <= strings[i] and dp[i - 1][1] < dp[i][0]:
