def find_box_number(chocolates_per_box, query_index):
    cumulative_sum = []
    current_sum = 1
    for chocolates in chocolates_per_box:
        current_sum += chocolates
        cumulative_sum.append(current_sum)

    def binary_search(num, start, end):
        if start >= end:
            return start
        mid = (start + end) // 2
        mid_val = cumulative_sum[mid]
        if num == mid_val:
            return mid
        if num > mid_val:
            return binary_search(num, mid + 1, end)
        else:
            return binary_search(num, start, mid)
    box_index = binary_search(query_index, 0, len(cumulative_sum) - 1)
    return box_index + 1

