def min_operations_to_permutation(A, N):
    A.sort()
    max_money = list(range(1, N + 1))
    count = 1
    flag = True
    for (i, j) in zip(A, max_money):
        if i == j:
            continue
        elif i < j:
            count += j - i
        else:
            flag = False
            break
    if flag:
        return count
    else:
        return -1

