def calculate_winning_arrays(t, test_cases):
    pri = 1000000008
    results = []
    for (n, k) in test_cases:
        g1 = pow(2, n, pri)
        g2 = pow(2, n, pri)
        if k == 0:
            results.append(1)
            continue
        ans = [0] * k
        if n % 2 == 0:
            ways = pow(2, n - 1, pri)
        else:
            ways = pow(2, n - 1, pri) + 1
        ans[0] = ways % pri
        for i in range(1, k):
            if n % 2 == 0:
                ways = pow(2, n - 1, pri)
            else:
                ways = pow(2, n - 1, pri) + 1
            ways %= pri
            if n % 2 == 0:
                ans[i] = ans[i - 1] * (ways - 1) % pri
                ans[i] += g2
                ans[i] %= pri
                g2 = g2 * g1 % pri
            else:
                ans[i] = ans[i - 1] * ways % pri
        results.append(ans[-1])
    return results

