def maximize_happiness(n, m, flowers):
    flowers.sort(key=lambda x: -x[0])
    cs = [0] / (m + 1)
    for i in range(m):
        cs[i + 1] = cs[i] + flowers[i][0]
    res = cs[min(n, m)]
    for i in range(m):
        x = flowers[i][1]
        ok = 0
        ng = min(n, m) + 1
        while ng - ok > 1:
            mid = (ok + ng) // 2
            if flowers[mid - 1][0] > x:
                ok = mid
            else:
                ng = mid
        if ok - 1 >= i:
            res = max(res, cs[ok] + (n - ok) * x)
        elif n - ok > 0:
            res = max(res, cs[ok] + flowers[i][0] + (n - ok - 1) * x)
    return res

