import math

def can_form_k_subarrays_with_mean_gcd(A, K):
    G = 1
    for num in A:
        G = math.gcd(G, num)
    ind = 0
    temp = 0
    N = len(A)
    while ind < N and temp < K:
        curr = 0
        while ind < N and curr != G:
            curr = math.gcd(curr, A[ind])
            ind += 1
        if curr == G:
            temp += 1
    return 'YES' if temp == K else 'NO'

