def get_distinct_difference(A: List[int], N: int=None) -> List[int]:
    if N is not None:
        N = len(A)
    dict_left = {}
    dict_right = {}
    left = [0] * N
    right = [0] * N
    ans = [0] * N
    (i, j) = (0, N - 1)
    while i < N and j >= 0:
        right[j] = len(dict_right)
        left[i] = len(dict_left)
        if A[i] not in dict_left:
            dict_left[A[i]] = 1
        if A[j] not in dict_right:
            dict_right[A[j]] = 1
        i += 1
        j -= 1
    for i in range(N):
        ans[i] = left[i] - right[i]
    return ans

