def calculate_average_sleep_per_week(n, k, sleep_times):
    current_sum = sum(sleep_times[:k])
    total_sum = current_sum
    for i in range(k, n):
        current_sum -= sleep_times[i] - sleep_times[i - k]
        total_sum += current_sum
    average_sleep = total_sum / (n - k + 1)
    return round(average_sleep, 6)

