def minimum_employees_to_inform(n, friendships):
    mat = [0] / n
    for (u, v) in friendships:
        mat[u - 1] |= 1 << v - 1
        mat[v - 1] |= 1 << u - 1
    for i in range(n):
        mat[i] |= 1 << i
    goal = 2 ** n - 1
    ans = n
    for i in range(1, goal + 1):
        mvs = 0
        loc = 0
        for j in range(n):
            if i & 1 << j:
                loc |= mat[j]
                mvs += 1
        if loc == goal:
            ans = min(mvs, ans)
    return ans

