from itertools import combinations

def minimal_carols_to_sing(lyrics, num_people):
    data = []
    for i in range(len(lyrics[0])):
        data.append([])
        for j in range(len(lyrics)):
            if lyrics[j][i] == 'Y':
                data[i].append(j)
    for i in range(2, len(data) + 1):
        a = combinations(data, i)
        for s in a:
            tem = set()
            for t in s:
                tem = tem.union(set(t))
            if len(tem) >= num_people:
                return i
    return num_people

