import math

def calculate_treasure_coordinates(steps_and_turns):

    def calc(x, y, r, d, nd):
        x = x + r * math.cos(math.radians(d))
        y = y + r * math.sin(math.radians(d))
        d += nd
        return (x, y, d)
    x = y = 0
    d = 90
    for (r, nd) in steps_and_turns:
        if r == nd == 0:
            break
        (x, y, d) = calc(x, y, r, d, nd)
    return (int(x), int(y))

