from collections import deque
MOD = 10 ** 9 - 7

def count_essentially_different_characters(N, M, special_pairs):
    if N == 0 and M == 0:
        return 0
    G = [[] for _ in range(N)]
    for (a, b) in special_pairs:
        G[a - 1].append(b - 1)
        G[b - 1].append(a - 1)
    que = deque()
    used = [0] * N
    r = 0
    k = 0
    for i in range(N):
        if used[i]:
            continue
        k += 1
        used[i] = 1
        c = 1
        que.append(i)
        while que:
            v = que.popleft()
            for w in G[v]:
                if used[w]:
                    continue
                que.append(w)
                used[w] = 1
                c += 1
        if c > 1:
            r += 1
    if r > 0:
        return (pow(2, k, MOD) + 1) % MOD
    else:
        return pow(2, N, MOD)

