import math

def can_prevent_arrival(N, D, Ds, Q, Qs):
    dp = [math.inf] / (N + 1)
    dp[-1] = 1
    for i in range(N - 1, -1, -1):
        if Ds[i] >= 2 * dp[i + 1]:
            dp[i] = dp[i + 1]
        else:
            dp[i] = dp[i + 1] + Ds[i]
    Pos = [D]
    for i in range(N):
        if Ds[i] > Pos[-1] * 2:
            Pos.append(Pos[-1])
        else:
            Pos.append(abs(Pos[-1] - Ds[i]))
    results = []
    for q in Qs:
        if dp[q] > Pos[q - 1]:
            results.append('NO')
        else:
            results.append('YES')
    return results

