def generate_unique_subsequences(s: str, k: int) -> list[str]:
    n = len(s)
    result = []
    for i in range(1, n, k):
        substring = s[i:i + k]
        unique_chars = []
        seen = set()
        for char in substring:
            if char not in seen:
                unique_chars.append(char)
                seen.add(char)
        result.append(''.join(unique_chars))
    return result

