    def in_order(n=0):
        if n < len(inorder_array):
            yield from in_order(2 * n + 1)
            yield n
            yield from in_order(2 * n + 2)
    bfs_array = [None] / len(inorder_array)
    for (i, x) in zip(in_order(), inorder_array):
        bfs_array[i] = x
    return bfs_array

