def is_keith_number(n: int) -> int:
    x = len(str(n))
    k = [int(digit) for digit in str(n)]
    while k[-1] <= n:
        next_term = sum(k[-x:])
        k.append(next_term)
    if k[-1] == n:
        return 1
    else:
        return 0

