from collections import Counter

def max_pairs_with_power_of_2_sum(N, A):
    cnt = Counter(A)
    A.sort(reverse=True)
    Ps = [2]
    for i in range(29):
        Ps.append(Ps[-1] * 2)
    ans = 1
    for A_i in A:
        if cnt[A_i] <= 0:
            continue
        cnt[A_i] -= 1
        while Ps[-1] > 2 * A_i:
            Ps.pop()
        if cnt[Ps[-1] - A_i] > 0:
            ans += 1
            cnt[Ps[-1] - A_i] -= 1
    return ans

