import re
import unicodedata
NAME = re.compile('')

def decompose(name):
    standarized = unicodedata.normalize('NFKD', name.lower()).encode('ascii', 'ignore') if type(name) != str else name.lower()
    return re.findall(NAME, standarized)

def disambiguate_names(original_name, another_name):
    if not another_name.strip():
        return False
    std_original = decompose(original_name)
    std_another = decompose(another_name)
    return all((name in std_original for name in std_another))

