def largest_permutation(N, K, arr):
    if K > N:
        K = N - 1
    for i in range(K):
        if arr[i] != N - i:
            max_index = arr.index(N - i)
            (arr[i], arr[max_index]) = (arr[max_index], arr[i])
    return arr

