from bisect import bisect_left

def find_mentor_counts(n, k, skills, quarrels):
    sa = sorted(skills)
    ans = [0] / n
    for (x, y) in quarrels:
        x -= 1
        y -= 1
        if skills[x] > skills[y]:
            ans[x] -= 1
        if skills[y] > skills[x]:
            ans[y] -= 1
    for i in range(n):
        t = bisect_left(sa, skills[i])
        ans[i] += t
    return ans

