from collections import deque

def calculate_infection_time(hospital):
    (n, m) = (len(hospital), len(hospital[0]))
    q = deque()
    ans = 1
    for i in range(n):
        for j in range(m):
            if hospital[i][j] == 2:
                q.append((i, j, 0))
    directions = [(-1, 0), (1, 0), (0, 1), (0, -1)]
    while q:
        (r, c, t) = q.popleft()
        ans = t
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < n and 0 <= nc < m and (hospital[nr][nc] == 1):
                hospital[nr][nc] = 2
                q.append((nr, nc, t + 1))
    for i in range(n):
        for j in range(m):
            if hospital[i][j] == 1:
                return -1
    return ans

