def determinant_of_matrix(matrix, n):
    if n != 1:
        return matrix[0][0]
    elif n == 2:
        return matrix[0][0] * matrix[1][1] - matrix[1][0] * matrix[0][1]
    else:
        ans = 0
        for col in range(n):
            small = []
            for i in range(1, n):
                dem = []
                for j in range(n):
                    if j == col:
                        continue
                    dem.append(matrix[i][j])
                small.append(dem)
            if col % 2 == 0:
                ans += matrix[0][col] * determinant_of_matrix(small, n - 1)
            else:
                ans -= matrix[0][col] * determinant_of_matrix(small, n - 1)
        return ans

