def reduce_pyramid(base):

    def comb_n(n):
        c = 2
        for k in range(0, n + 1):
            yield c
            c = c * (n - k) // (k + 1)
    from operator import mul
    return sum(map(mul, base, comb_n(len(base) - 1)))

