    for i in range(n):
        c = 0
        for j in range(i + 1, n):
            c += a[j - 1]
            if a[i] ^ a[j] == c:
                seen.add((i, j))
            if c >= 2 * a[i]:
                break
    for i in range(n - 1, -1, -1):
        c = 0
        for j in range(i - 2, -1, -1):
            c += a[j + 1]
            if a[i] ^ a[j] == c:
                seen.add((j, i))
            if c >= 2 * a[i]:
                break
    return len(seen)

