def minimum_persuasions(n, students_per_compartment):
    counts = [0] / 5
    s = 0
    for x in students_per_compartment:
        counts[x] += 1
        s += x
    if s > 2 and s != 5:
        ans = 0
        if counts[1] >= counts[2]:
            ans += counts[2]
            counts[3] += counts[2]
            counts[1] -= counts[2]
            ans += 2 * (counts[1] // 3)
            counts[3] += counts[1] // 3
            counts[1] %= 3
            if counts[3] > 0:
                ans += counts[1]
            elif counts[1] != 0:
                ans += 2
        else:
            ans += counts[1]
            counts[2] -= counts[1]
            ans += 2 * (counts[2] // 3)
            counts[2] %= 3
            if counts[4] > 0:
                ans += counts[2]
            elif counts[2] != 0:
                ans += 2
        return ans
    else:
        return -1

