def decode_message(s: str, k: int) -> str:
    (cnt1, cnt2) = (0, 0)
    for i in range(len(s)):
        if s[i] == '*':
            cnt1 += 1
        if s[i] == '?':
            cnt2 += 1
    if len(s) - (cnt1 + cnt2) <= k:
        if cnt1 == 0:
            return 'Impossible'
        else:
            ex = k - (len(s) - (cnt1 + cnt2))
            s1 = ''
            ch = 0
            for i in range(len(s)):
                if ch == 0 and s[i] != '*' and (s[i] != '?'):
                    if i + 1 < len(s) and s[i + 1] == '*':
                        for j in range(ex + 1):
                            s1 += s[i]
                        ch = 1
                    else:
                        s1 += s[i]
                elif s[i] != '*' and s[i] != '?':
                    s1 += s[i]
            return s1
    elif len(s) - (cnt1 + cnt2) > k:
        if len(s) - 2 * (cnt1 + cnt2) > k:
            return 'Impossible'
        else:
            ex = len(s) - (cnt1 + cnt2) - k
            s1 = ''
            for i in range(len(s)):
                if s[i] != '*' and s[i] != '?':
                    if i + 1 < len(s) and (s[i + 1] == '*' or s[i + 1] == '?'):
                        if ex != 0:
                            ex -= 1
                        else:
                            s1 += s[i]
                    else:
                        s1 += s[i]
            return s1
    else:
        s1 = ''
        for i in range(len(s)):
            if s[i] != '*' and s[i] != '?':
                s1 += s[i]
        return s1

