from collections import Counter

def max_gift_size(candies):
    cnt = list(Counter(candies).items())
    cnt.sort(key=lambda k: k[1], reverse=True)
    prev = len(candies) - 1
    ans = 0
    for (_, c) in cnt:
        if prev > c:
            ans += c
            prev = c
        else:
            ans += prev - 1
            prev -= 1
        if prev == 0:
            break
    return ans

