        if (p - x1) * d <= 0:
            tt = (x2 - p) * d * t1
        else:
            tt = (2 * s + (x2 - p) * d) * t1
    elif d == 1:
        tt = (-2 * s - (p + x2)) * t1
    else:
        tt = (p + x2) * t1
    return min(ti, tt)
